class CrciState extends React.Component<{}, { count: any }> {

  handleclick() {
    var test;
    getscore().then((result) => {
      console.log("Result :", result);
      test = result.matches;
      let teams = [];

      for (var key of Object.keys(test)) {
        if (key === "team-1")
            teams.push(test[key]);
        if (key === "team-2")
            teams.push(test[key]);
}

      this.setState({ count: test })
      return result.matches;
    })  

  }


  constructor(props: any) {
    super(props);

    this.handleclick = this.handleclick.bind(this)

    this.state = {
      count : [
    "red",
    "green",
    "yellow",
    "blue",
    "purple",
    "brown",
    "orange",
    "pink",
    "aqua",
    "lime"
  ]
    }
  }

  render() {
    return (
      <div>
        <h1> Matches </h1>
        {/* <h1> {this.state.count} </h1> */}


        <ul>
      {this.state.count.map((value: {} | null | undefined) => {
        return <li> {value} </li>
      })}
        </ul>


        <button onClick={this.handleclick} > Get Matches </button>
      </div>
    )
  }
}


