import * as React from 'react';
import ScoreCard from './ScoreCard';


class CrciState extends React.Component<{}, { count: any }> {

  handleclick() {
    getscore().then((result) => {
      console.log("Results :", result.matches[0].date);
      let teams: any[] = [];
      let Twenty20Matches = [];

      result.matches.map((s: any) => {
        if (s["type"] == "Twenty20") {
          teams.push((JSON.stringify(s)));
        }
        })

      console.log("Teams :" + teams);

      teams.forEach((t) => {
        var team = JSON.parse(t);
        if(team["type"] == "Twenty20") { 
          Twenty20Matches.push(team["team-1"] + " VS " + team["team-2"]);
        }

      })

      this.setState({ count: teams })
      return result.matches;
    })  

  }


  constructor(props: any) {
    super(props);

    this.handleclick = this.handleclick.bind(this)

    this.state = {
      count : [
  ]
    }
  }

  render() {
    return (
      <div>
        <h1> Matches </h1>
        <ul>
          {this.state.count.map((value: {} | null ) => {
            if (value) {
              return <li> {value} </li>
            }
      })}
        </ul>
        <ScoreCard />
        <button onClick={this.handleclick} > Get Matches </button>
      </div>
    )
  }
}


