import * as React from 'react';

export interface Match {
  unique_id:    number;
  date:         Date;
  dateTimeGMT:  Date;
  "team-1":     string;
  "team-2":     string;
  squad:        boolean;
  matchStarted: boolean;
  type:         string;
}

// let jsonObj: any = JSON.parse(employeeString); // string to generic object first
// let employee: Employee = <Employee>jsonObj;
class CrciState extends React.Component<{}, { count: any }> {

  handleclick() {
    getscore().then((result) => {
      console.log("Results :" , result);
      let teams = [];
      let Twenty20Matches: string[] = [];

      for (var value of Object.values(result.matches)) {
        teams.push(JSON.stringify(value));
        // console.log(value.type);
      }
      console.log("Teams :" + teams)

      teams.forEach((t) => {
        var team = JSON.parse(t);
        if(team["type"] == "Twenty20") { 
          Twenty20Matches.push(team["team-1"] + " VS " + team["team-2"]);
        }

      })

      this.setState({ count: Twenty20Matches })
      return result.matches;
    })  

  }


  constructor(props: any) {
    super(props);

    this.handleclick = this.handleclick.bind(this)

    this.state = {
      count : [
  ]
    }
  }

  render() {
    return (
      <div>
        <h1> Matches </h1>
        {/* <h1> {this.state.count} </h1> */}
        <ul>
          {this.state.count.map((value: {} | null ) => {
            if (value) {
              return <li> {value} </li>
            }
      })}
        </ul>
        <button onClick={this.handleclick} > Get Matches </button>
      </div>
    )
  }
}


export const getscore = async (): Promise<any> => {
  var val;
  const endpoint = `https://cricapi.com/api/matches?apikey=UY8BE0Sq46gdOnLNp97cfVa29pA2`;
